mapper.select:
  		SELECT PRODUCT_ID, PNAME, UNIT_PRICE, DESCRIPTION, MANUFACTURER, CATEGORY, UNITS_IN_STOCK, CONDITION, FILENAME
		FROM PRODUCT
		WHERE PRODUCT_ID = #{productId}

mapper.update:
  		UPDATE PRODUCT
		SET PNAME=#{pname}, UNIT_PRICE=#{unitPrice}, DESCRIPTION=#{description}, MANUFACTURER=#{manufacturer}, CATEGORY=#{category}, UNITS_IN_STOCK=#{unitsInStock}, CONDITION=#{condition}
		WHERE PRODUCT_ID = #{productId}
